import React, { Component } from "react";
import Title from "../../components/General/Title";
import { Redirect } from "react-router-dom";
import axios from "axios";

export default class AccountEdit extends Component {
    constructor(props) {
        super(props);
        // Assign state itself, and a default value for items
        this.state = {
            email_address: "",
            shipping_address: [],
            id: "",
            first_name: "",
            last_name: "",
        };

        this.redirect = false;
    }

    async componentDidMount() {
        await this.loadUserValues();
    }

    loadUserValues() {
        return new Promise((resolve) => {
            axios
                .get(
                    "http://localhost:4000/catweallgetalong/sessions/user-info"
                )
                .then((response) => {
                    this.setState(
                        {
                            shipping_address: response.data[0].shipping_address,
                            email_address: response.data[0].email_address,
                            first_name: response.data[0].first_name,
                            last_name: response.data[0].last_name,
                            id: response.data[0]._id,
                        },
                        () => {
                            console.log(this.state.shipping_address);
                            console.log(this.state.email_address);
                            console.log(this.state.first_name);
                            console.log(this.state.last_name);
                            console.log(this.state.id);
                        }
                    );
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    }

    removeSession() {
        return new Promise((resolve) => {
            axios
                .post("http://localhost:4000/catweallgetalong/sessions/logout")
                .then((response) => {
                    if (response === "out") this.sessionAdded = true;
                    resolve("resolved");
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    }

    async logoutUser() {
        // remove session
        await this.removeSession();
        // redirect to "/"
        this.redirect = true;
        this.forceUpdate();
        // refresh page (will eliminate cart!)
        window.location.reload(false);
    }

    render() {
        if (this.redirect) {
            return <Redirect push to="/" />;
        }
        return (
            <React.Fragment>
                <div>
                    <div
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "flex-end",
                        }}
                    >
                        <button
                            type="button"
                            className="btn btn-danger"
                            onClick={this.logoutUser.bind(this)}
                        >
                            Logout
                        </button>
                    </div>
                    <Title name="Edit" title="Account" />
                </div>
            </React.Fragment>
        );
    }
}
