import React, { Component } from "react";
import { ProductConsumer } from "../../context-product";
import { Redirect } from "react-router-dom";
import Title from "../General/Title";
import axios from "axios";

export default class Checkout extends Component {
    constructor(props) {
        super(props);
        // Assign state itself, and a default value for items
        this.directTo = "/account";
    }

    sessionCheck() {
        return new Promise((resolve) => {
            axios
                .get("http://localhost:4000/catweallgetalong/sessions/")
                .then((res) => {
                    //do something
                    if (res.data === true) {
                        this.directTo = "/checkout";
                    } else {
                        this.directTo = "/account";
                    }

                    resolve("resolved");
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    }

    async componentDidMount() {
        await this.sessionCheck();
        this.forceUpdate();
    }

    render() {
        return (
            <section>
                <Title name="Confirm" title="Checkout" />
                <ProductConsumer>
                    {(value) => {
                        const { cart } = value;
                        if (cart.length < 1)
                            return <Redirect push to="/cart" />;
                    }}
                </ProductConsumer>
                <HandleDisplayErrors
                    display_errors={this.display_errors}
                    address_verified={this.address_verified}
                    email_verified={this.email_verified}
                    email_isDuplicate={this.email_isDuplicate}
                    password_is8Chars={this.password_is8Chars}
                    name_isInputted={this.name_isInputted}
                />
                <form>
                    <div className="form-group">
                        <label htmlFor="addLine1">Address Line 1</label>
                        <input
                            type="address"
                            className="form-control"
                            id="addLine1"
                            placeholder="3901 SW 154th Ave"
                            onChange={(event) =>
                                this.setState({
                                    address_line1: event.target.value,
                                })
                            }
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="addLine2">Address Line 2</label>
                        <input
                            type="address"
                            className="form-control"
                            id="addLine2"
                            placeholder="closest under the street"
                            onChange={(event) =>
                                this.setState({
                                    address_line2: event.target.value,
                                })
                            }
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="secondary">Apt / Suite / Other</label>
                        <input
                            type="address"
                            className="form-control"
                            id="secondary"
                            placeholder="Apt# 314"
                            onChange={(event) =>
                                this.setState({
                                    address_secondary: event.target.value,
                                })
                            }
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="city">City</label>
                        <input
                            type="address"
                            className="form-control"
                            id="city"
                            placeholder="Davie"
                            onChange={(event) =>
                                this.setState({
                                    address_city: event.target.value,
                                })
                            }
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="zip">Zipcode</label>
                        <input
                            type="address"
                            className="form-control"
                            id="zip"
                            placeholder="33331"
                            onChange={(event) =>
                                this.setState({
                                    address_zipcode: event.target.value,
                                })
                            }
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="state" className="col-s control-label">
                            State
                        </label>
                        <div className="col-s">
                            <select
                                className="form-control"
                                id="state"
                                name="state"
