import React, { Component } from "react";
import Title from "../../components/General/Title";
import { Redirect } from "react-router-dom";
import axios from "axios";

export default class AccountEdit extends Component {
    constructor(props) {
        super(props);
        // Assign state itself, and a default value for items
        this.state = {
            address_line1: "",
            address_line2: "",
            address_secondary: "",
            address_zipcode: "",
            address_city: "",
            address_state: "",

            email_address: "",
            id: "",
            first_name: "",
            last_name: "",
        };

        this.redirect = false;
    }

    async componentDidMount() {
        await this.loadUserValues();
    }

    loadUserValues() {
        return new Promise((resolve) => {
            axios
                .get(
                    "http://localhost:4000/catweallgetalong/sessions/user-info"
                )
                .then((response) => {
                    this.setState(
                        {
                            address_line1:
                                response.data[0].shipping_address[0].street1,
                            address_line2:
                                response.data[0].shipping_address[0].street2,
                            address_secondary:
                                response.data[0].shipping_address[0].secondary,
                            address_zipcode:
                                response.data[0].shipping_address[0].zipcode,
                            address_city:
                                response.data[0].shipping_address[0].city,
                            address_state:
                                response.data[0].shipping_address[0].state,
                            email_address: response.data[0].email_address,
                            first_name: response.data[0].first_name,
                            last_name: response.data[0].last_name,
                            id: response.data[0]._id,
                        },
                        () => {
                            console.log(this.state.address_line1);
                            console.log(this.state.address_line2);
                            console.log(this.state.address_secondary);
                            console.log(this.state.address_zipcode);
                            console.log(this.state.address_city);
                            console.log(this.state.address_state);
                            console.log(this.state.email_address);
                            console.log(this.state.first_name);
                            console.log(this.state.last_name);
                            console.log(this.state.id);
                        }
                    );
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    }

    removeSession() {
        return new Promise((resolve) => {
            axios
                .post("http://localhost:4000/catweallgetalong/sessions/logout")
                .then((response) => {
                    if (response === "out") this.sessionAdded = true;
                    resolve("resolved");
                })
                .catch(function (error) {
                    console.log(error);
                });
        });
    }

    async logoutUser() {
        // remove session
        await this.removeSession();
        // redirect to "/"
        this.redirect = true;
        this.forceUpdate();
        // refresh page (will eliminate cart!)
        window.location.reload(false);
    }

    async handleOnSubmit(event) {
        event.preventDefault();

        // verify (shipping address, email_address, names Inputted)
        // post accounts update by id
        // add session again so new info can be loaded to the session variable in server.js
    }

    render() {
        if (this.redirect) {
            return <Redirect push to="/" />;
        }
        return (
            <React.Fragment>
                <div>
                    <div
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "flex-end",
                        }}
                    >
                        <button
                            type="button"
                            className="btn btn-danger"
                            onClick={this.logoutUser.bind(this)}
                        >
                            Logout
                        </button>
                    </div>
                    <Title name="Edit" title="Account" />

                    <form>
                        <div className="form-group">
                            <label htmlFor="inputFirstName">First Name</label>
                            <input
                                type="name"
                                className="form-control"
                                id="inputFirstName"
                                placeholder="John"
                                onChange={(event) => {
                                    this.setState({
                                        first_name: event.target.value,
                                    });
                                }}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputLastName">Last Name</label>
                            <input
                                type="name"
                                className="form-control"
                                id="inputLastName"
                                placeholder="Doe"
                                onChange={(event) =>
                                    this.setState({
                                        last_name: event.target.value,
                                    })
                                }
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputEmail">Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                id="inputEmail"
                                aria-describedby="emailHelp"
                                placeholder="Enter email"
                                onChange={(event) => {
                                    this.setState({
                                        email_address: event.target.value.toLowerCase(),
                                    });
                                }}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="addLine1">Address Line 1</label>
                            <input
                                type="address"
                                className="form-control"
                                id="addLine1"
                                placeholder="3901 SW 154th Ave"
                                onChange={(event) =>
                                    this.setState({
                                        address_line1: event.target.value,
                                    })
                                }
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="addLine2">Address Line 2</label>
                            <input
                                type="address"
                                className="form-control"
                                id="addLine2"
                                placeholder="closest under the street"
                                onChange={(event) =>
                                    this.setState({
                                        address_line2: event.target.value,
                                    })
                                }
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="secondary">
                                Apt / Suite / Other
                            </label>
                            <input
                                type="address"
                                className="form-control"
                                id="secondary"
                                placeholder="Apt# 314"
                                onChange={(event) =>
                                    this.setState({
                                        address_secondary: event.target.value,
                                    })
                                }
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="city">City</label>
                            <input
                                type="address"
                                className="form-control"
                                id="city"
                                placeholder="Davie"
                                onChange={(event) =>
                                    this.setState({
                                        address_city: event.target.value,
                                    })
                                }
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="zip">Zipcode</label>
                            <input
                                type="address"
                                className="form-control"
                                id="zip"
                                placeholder="33331"
                                onChange={(event) =>
                                    this.setState({
                                        address_zipcode: event.target.value,
                                    })
                                }
                            />
                        </div>
                        <div className="form-group">
                            <label
                                htmlFor="state"
                                className="col-s control-label"
                            >
                                State
                            </label>
                            <div className="col-s">
                                <select
                                    className="form-control"
                                    id="state"
                                    name="state"
                                    onChange={(event) =>
                                        this.setState({
                                            address_state: event.target.value,
                                        })
                                    }
                                >
                                    <option value="">N/A</option>
                                    <option value="AK">Alaska</option>
                                    <option value="AL">Alabama</option>
                                    <option value="AR">Arkansas</option>
                                    <option value="AZ">Arizona</option>
                                    <option value="CA">California</option>
                                    <option value="CO">Colorado</option>
                                    <option value="CT">Connecticut</option>
                                    <option value="DC">
                                        District of Columbia
                                    </option>
                                    <option value="DE">Delaware</option>
                                    <option value="FL">Florida</option>
                                    <option value="GA">Georgia</option>
                                    <option value="HI">Hawaii</option>
                                    <option value="IA">Iowa</option>
                                    <option value="ID">Idaho</option>
                                    <option value="IL">Illinois</option>
                                    <option value="IN">Indiana</option>
                                    <option value="KS">Kansas</option>
                                    <option value="KY">Kentucky</option>
                                    <option value="LA">Louisiana</option>
                                    <option value="MA">Massachusetts</option>
                                    <option value="MD">Maryland</option>
                                    <option value="ME">Maine</option>
                                    <option value="MI">Michigan</option>
                                    <option value="MN">Minnesota</option>
                                    <option value="MO">Missouri</option>
                                    <option value="MS">Mississippi</option>
                                    <option value="MT">Montana</option>
                                    <option value="NC">North Carolina</option>
                                    <option value="ND">North Dakota</option>
                                    <option value="NE">Nebraska</option>
                                    <option value="NH">New Hampshire</option>
                                    <option value="NJ">New Jersey</option>
                                    <option value="NM">New Mexico</option>
                                    <option value="NV">Nevada</option>
                                    <option value="NY">New York</option>
                                    <option value="OH">Ohio</option>
                                    <option value="OK">Oklahoma</option>
                                    <option value="OR">Oregon</option>
                                    <option value="PA">Pennsylvania</option>
                                    <option value="PR">Puerto Rico</option>
                                    <option value="RI">Rhode Island</option>
                                    <option value="SC">South Carolina</option>
                                    <option value="SD">South Dakota</option>
                                    <option value="TN">Tennessee</option>
                                    <option value="TX">Texas</option>
                                    <option value="UT">Utah</option>
                                    <option value="VA">Virginia</option>
                                    <option value="VT">Vermont</option>
                                    <option value="WA">Washington</option>
                                    <option value="WI">Wisconsin</option>
                                    <option value="WV">West Virginia</option>
                                    <option value="WY">Wyoming</option>
                                </select>
                            </div>
                        </div>
                        <button
                            className="btn btn-primary"
                            onClick={this.handleOnSubmit.bind(this)}
                        >
                            Edit Account
                        </button>
                    </form>
                </div>
            </React.Fragment>
        );
    }
}
